<?phpnamespace Api\Model;use Think\Model;class FieldRentModel extends Model {	function register($data){		$rules = array(			array('userCd','require','请输入姓名'),			array('userName','require','请输入手机号码'),			array('password','require','请选择出发地')	   );	                  		$this->validate($rules)->add($data);		return true;	}		function getInfo($id)	{		$where = 'fieldId = "'.$id.'"';		$m = $this->field('fieldId,rentNo,startDate,endDate,rentAmount,contractFile')->where($where)->find();		return $m;	}}?>